package User.Validator.JUnit.Test;

import java.util.regex.Matcher;
import java.util.regex.Pattern;

public class UserRegistrationJUnit {
	IValidate firstNameValidation = n -> {
		return Pattern.compile("^[A-Z]+[a-z A-Z]{2,}").matcher(n).find() ? true : false;
	};
	
	IValidate lastNameValidation = n -> {
		return Pattern.compile("^[A-Z]+[a-z A-Z]{2,}").matcher(n).find() ? true : false;
	};
	
	IValidate mobileNumberValidation = n -> {
		return Pattern.compile("^[0-9]{2}\\s[0-9]{10}$").matcher(n).find() ? true : false;
	};
	
	IValidate emailValidation = n -> {
		return Pattern.compile("^[A-Z]+([+._-]{0,1}([A-Z 0-9]+))*[@][A-Z 0-9]{1,}[.][A-Z]{2,}([.][A-Z]{2,}){0,1}$", Pattern.CASE_INSENSITIVE).matcher(n).find() ? true : false;
	};
	
	IValidate passwordRule1Validation = n -> {
		return Pattern.compile("[A-Z a-z 0-9]{8,}").matcher(n).find() ? true : false;
	};
	
	IValidate passwordRule2Validation = n -> {
		return Pattern.compile("^[A-Z a-z]*(?=.*[A-Z])[A-Z a-z 0-9]*${8,}").matcher(n).find() ? true : false;
	};
	
	IValidate passwordRule3Validation = n -> {
		return Pattern.compile("^[A-Z a-z]*(?=.*[A-Z])(?=.*[0-9])[A-Z a-z 0-9]*${8,}").matcher(n).find() ? true : false;
	};
	
	IValidate passwordRule4Validation = n -> {
		return (Pattern.compile("^[A-Z a-z]*(?=.*[A-Z])(?=.*[0-9])[A-Z a-z 0-9]*.{8,}$").matcher(n).find() && Pattern.compile("^(?=.*[0-9 A-Z a-z])[0-9 A-Z a-z]*[$&+,:;=?@#|'<>.-^*()%!][0-9 A-Z a-z]*$").matcher(n).find()) ? true : false;
	};
}